mapper.select:
select prodname, price, submon, caddr, subvol, storename, condition
from salestate
where cid = #{cid}

mapper.update:
update cmember set cid = #{cid}, cpw = #{cpw}, cph = #{cph}, czipcode = #{czipcode}, caddr = #{caddr}, cardnumber = #{cardnumber},
bank = #{bank}, EXPIRATIONDATE = #{expirationDate}
where cid = #{loginId}

update salestate set condition = '취소요청'
where cid = #{loginId}
and prodname = #{cname}
