mapper.select:
SELECT prodname, flavor, price 
FROM prodregis 
WHERE prodname=#{prodname}

SELECT caddr 
FROM cmember 
WHERE cid=#{cid}

SELECT bid  
FROM prodregis 
WHERE prodname =#{prodname}

select * from prodregis

 where caffeine = #{caffeine}


 and drip = #{drip}


 and heavy = #{heavy}


 and flavor = #{flavor}


 and acidity = #{acidity}


 and origin = #{origin}


 and pricerange = #{pricerange}
